mapper.update:
update Pet set pet_name =
#{pet_name}, pet_species =
#{pet_species},pet_gender = #{pet_gender},
pet_age =
#{pet_age},pet_file = #{pet_file}
where pet_no = #{pet_no}

mapper.select:
select count(*) from (
select FreeBoard_boardno from FreeBoard where
FreeBoard_userid = #{user_id}
UNION ALL
select TipBoard_boardno from
TipBoard where TipBoard_userid = #{user_id}
UNION ALL
select
meeting_boardno from MeetingBoard where MeetingBoard_userid =
#{user_id}
) cnt;

select count(*) from (
select fb.FreeBoard_boardno from FreeBoard fb, FreeLikes fl where
fb.FreeBoard_userid = fl.FreeLikes_userid and fb.userid = #{user_id}
UNION
select tb.TipBoard_boardno from TipBoard tb, TipLikes tl where
tb.TipBoard_id = tl.TipLikes_id and tb.TipBoard_id = #{user_id}
UNION

select mb.meeting_boardno from meetingBoard mb , meetingLikes ml where
mb.meetingBoarduserid = ml.meetingLikes_userid and
mb.meetingBoarduserid= #{user_id}
UNION

select mr.meeting_boardno from meetingReview mr , meetingLikes mtl where
mr.meetingReview_userid = mtl.meetingLikes_userid and
mr.meetingReview_userid = #{user_id}
) cnt;
