mapper.select:
		select * from FreeBoard where FreeBoard_boardname =
		#{FreeBoard_boardname}
		and FreeBoard_boardno =#{FreeBoard_boardno}

		select * from FreeBoard where FreeBoard_boardname =
		#{FreeBoard_boardname}
		and FreeBoard_userId = #{FreeBoard_userId}

select * from FreeBoard where 1 = 0 and FreeBoard_boardname =
#{FreeBoard_boardname}

	or FreeBoard_title like '%${title}%' 

	or FreeBoard_content like '%${content}%' 

	or FreeBoard_userId like #{nickname }

mapper.update:
		update FreeBoard set
		
		,
		FreeBoard_file=#{file},
		
		,
		FreeBoard_readCount=#{FreeBoard_readCount}, #{FreeBoard_contentPic}
		where FreeBoard_boardname = #{FreeBoard_boardname}
		and FreeBoard_boardno =#{FreeBoard_boardno}
